
public class Ch3Ex3BoucleWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	             
	    	int compteur = 1;         
	    	
	    	while (compteur <= 10) {              
	    		if (compteur%2 == 1)                   
	    			System.out.println("****")     ;                  
	    		else                       
	    			System.out.println("++++++++")     ;                 
	    		++compteur ;         } 
	    }
	}


